const items = $input.all();
if (!items || items.length === 0) {
  throw new Error('No data from Google Sheets');
}
const headers = Object.keys(items[0].json);
const csvRows = [headers.join(',')];
const sampleSize = 5;
const sampleItems = items.slice(0, sampleSize);
sampleItems.forEach(item => {
  const row = headers.map(header => {
    let value = String(item.json[header] || '');
    if (value.includes(',') || value.includes('"') || value.includes('\n')) {
      value = '"' + value.replace(/"/g, '""') + '"';
    }
    return value;
  });
  csvRows.push(row.join(','));
});
return {
  json: {
    csv_content: csvRows.join('\n'),
    total_rows: items.length,
    sample_rows: sampleItems.length,
    product_context: 'tools and equipment',
    sample: sampleItems.length
  }
};

const response = $input.first().json;
if (response.success) {
  const fileId = response.file_id || '';
  return {
    json: {
      success: true,
      message: 'Translation completed! Ready to download.',
      file_id: fileId,
      download_url: `http://host.docker.internal:5000/download/${fileId}`,
      translation_details: {
        total_processed: response.result?.total_processed || 0,
        successful: response.result?.successful || 0,
        failed: response.result?.failed || 0,
        output_size: response.output_size
      }
    }
  };
} else {
  return {
    json: {
      success: false,
      error: response.error || 'Translation failed'
    }
  };
}

const translationData = $input.first().json;
const downloadData = $input.last();

return {
  json: {
    success: true,
    message: 'Translation completed and file downloaded successfully!',
    translation_summary: {
      total_processed: translationData.translation_details?.total_processed || 'unknown',
      successful: translationData.translation_details?.successful || 'unknown',
      failed: translationData.translation_details?.failed || 0,
      output_size: translationData.translation_details?.output_size
    },
    file_info: {
      downloaded: downloadData.binary ? 'Yes' : 'No',
      file_size: downloadData.binary?.data?.length || 0,
      filename: 'translated_products.xlsx'
    },
    arabic_content_generated: [
      'Arabic SEO titles optimized for search engines',
      'Arabic SEO descriptions for better visibility',
      'Arabic product names for local market appeal'
    ],
    completion_message: [
      'Your tools and equipment products now have Arabic translations!',
      'Ready for Arabic-speaking markets',
      'SEO-optimized for better search rankings',
      'Download the Excel file from this execution result'
    ]
  },
  binary: downloadData.binary
};
